let html = fs.readFileSync('index.html', 'utf8');

// Find the closing ]; of the medicines array
const marker = '        ];';
const markerIdx = html.lastIndexOf(marker);

if (markerIdx === -1) {
    console.error('Could not find closing ]; of medicines array');
    process.exit(1);
}

// Build the new medicines string
const newMedStr = newMedicines.map(med => {
    const altsStr = med.alternatives.map(a =>
        `                    { name: "${a.name}", price: "${a.price}" }`
    ).join(',\n');
    return `            {
                brand: "${med.brand}",
                generic: "${med.generic}",
                category: "${med.category}",
                usage: "${med.usage}",
                priceRange: "${med.priceRange}",
                alternatives: [
${altsStr}
                ]
            }`;
}).join(',\n');

// Insert before the closing ];
const insertPoint = markerIdx;
const before = html.slice(0, insertPoint);
const after = html.slice(insertPoint);

const newHtml = before + ',\n' + newMedStr + '\n' + after;

fs.writeFileSync('index.html', newHtml);
