// Read index.html
const html = fs.readFileSync('index.html', 'utf8');

// Find the medicines array using bracket counting (same as generate-pages.js)
const startMarker = '// ===== Medicine Database =====';
const startIdx = html.indexOf(startMarker);
const arrayStart = html.indexOf('const medicines = [', startIdx);
let depth = 0, i = html.indexOf('[', arrayStart);
const jsonStart = i;
for (; i < html.length; i++) {
    if (html[i] === '[') depth++;
    else if (html[i] === ']') { depth--; if (depth === 0) break; }
}
const arrayClosePos = i; // position of the ] that closes the medicines array

if (arrayClosePos === -1 || arrayClosePos >= html.length) {
    console.error('Could not find end of medicines array');
    process.exit(1);
}

console.log(`Found medicines array closing ] at position ${arrayClosePos}`);

// Build the new medicines string
const newMedStr = newMedicines.map(med => {
    const altsStr = med.alternatives.map(a =>
        `                    { name: "${a.name}", price: "${a.price}" }`
    ).join(',\n');
    return `            {
                brand: "${med.brand}",
                generic: "${med.generic}",
                category: "${med.category}",
                usage: "${med.usage}",
                priceRange: "${med.priceRange}",
                alternatives: [
${altsStr}
                ]
            }`;
}).join(',\n');

// Insert before the closing ] of the medicines array
const before = html.slice(0, arrayClosePos);
const after = html.slice(arrayClosePos);

const newHtml = before + ',\n' + newMedStr + '\n' + after;

fs.writeFileSync('index.html', newHtml);
console.log(`✅ Added ${newMedicines.length} new medicines to index.html`);
